library(widyr)
nearest_neighbors &lt;- function(df, feat, doc=F) {
  inner_f &lt;- function() {
    widely(
        ~ {
          y &lt;- .[rep(feat, nrow(.)), ]
          res &lt;- rowSums(. * y) / 
            (sqrt(rowSums(. ^ 2)) * sqrt(sum(.[feat, ] ^ 2)))

          matrix(res, ncol = 1, dimnames = list(x = names(res)))
        },
        sort = TRUE
    )}
  if (doc) {
    df |&gt; inner_f()(doc, dimension, value) }
  else {
    df |&gt; inner_f()(word, dimension, value)
  }
}


